INSERT INTO `galleries` (`id`, `title`, `description`, `image_path`, `author`, `date_taken`, `location`, `created_at`, `updated_at`) VALUES
(27, 'Fiona', '<p style=\"margin: 0.5em 0px 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">Hari jadi Kota Magelang ditetapkan pada tanggal 11 April 907 Masehi, berdasarkan Peraturan Daerah Kota Magelang Nomor 6 Tahun 1989. Penetapan ini merupakan tindak lanjut dari seminar dan diskusi yang dilaksanakan oleh Panitia Peneliti Hari Jadi Kota Magelang bekerja sama dengan&nbsp;<a href=\"https://id.wikipedia.org/wiki/Universitas_Tidar\" title=\"Universitas Tidar\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">Universitas Tidar Magelang</a>&nbsp;dibantu pakar sejarah dan arkeologi&nbsp;<a href=\"https://id.wikipedia.org/wiki/Universitas_Gadjah_Mada\" title=\"Universitas Gadjah Mada\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">Universitas Gadjah Mada</a>, M.M. Soekarto Kartoatmodjo, dilengkapi berbagai penelitian di Museum Nasional maupun Museum Radya Pustaka-Surakarta.</p><p style=\"margin: 0.5em 0px 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">Kota Magelang mengawali sejarahnya sebagai desa perdikan Mantyasih, yang saat ini dikenal dengan Kampung Meteseh di&nbsp;<a href=\"https://id.wikipedia.org/wiki/Magelang,_Magelang_Tengah,_Magelang\" title=\"Magelang, Magelang Tengah, Magelang\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">Kelurahan Magelang</a>. Di kampung Meteseh terdapat sebuah lumpang batu yang diyakini sebagai tempat upacara penetapan Sima atau Perdikan. Untuk menelusuri kembali sejarah Kota Magelang, sumber prasasti yang digunakan adalah&nbsp;<a href=\"https://id.wikipedia.org/wiki/Prasasti_Poh\" title=\"Prasasti Poh\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">prasasti Poh</a>,&nbsp;<a href=\"https://id.wikipedia.org/wiki/Prasasti_Gilikan\" title=\"Prasasti Gilikan\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">prasasti Gilikan</a>&nbsp;dan&nbsp;<a href=\"https://id.wikipedia.org/wiki/Prasasti_Mantyasih\" title=\"Prasasti Mantyasih\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">prasasti Mantyasih</a>. Ketiganya merupakan&nbsp;<a href=\"https://id.wikipedia.org/wiki/Prasasti\" title=\"Prasasti\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">prasasti</a>&nbsp;yang ditulis di atas lempengan tembaga.</p><p style=\"margin: 0.5em 0px 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">Prasasti Poh dan Mantyasih ditulis masa Mataram Hindu saat pemerintahan Raja&nbsp;<a href=\"https://id.wikipedia.org/wiki/Dyah_Balitung\" title=\"Dyah Balitung\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">Rake Watukura Dyah Balitung</a>&nbsp;(898-910 M), dalam prasasti ini disebut adanya Desa Mantyasih dan nama Desa Glangglang. Mantyasih inilah yang kemudian berubah menjadi Meteseh, sedangkan Glangglang berubah menjadi Magelang.</p><p style=\"margin: 0.5em 0px 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">Prasasti Mantyasih berisi antara lain, penyebutan nama Raja Rake Watukura Dyah Balitung, serta penyebutan angka 829 Çaka bulan Çaitra tanggal 11 Paro-Gelap Paringkelan Tungle, Pasaran Umanis hari Senais Sçara atau Sabtu, dengan kata lain Hari Sabtu Legi tanggal 11 April 907. Dalam Prasasti ini disebut pula Desa Mantyasih yang ditetapkan oleh Sri Maharaja Rake Watukura Dyah Balitung sebagai Desa Perdikan atau daerah bebas pajak yang dipimpin oleh pejabat patih. Juga disebut-sebut&nbsp;<i>Gunung Susundara</i>&nbsp;dan&nbsp;<i>Wukir Sumbing</i>&nbsp;yang kini dikenal dengan&nbsp;<a href=\"https://id.wikipedia.org/wiki/Gunung_Sindoro\" title=\"Gunung Sindoro\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">Gunung Sindoro</a>&nbsp;dan&nbsp;<a href=\"https://id.wikipedia.org/wiki/Gunung_Sumbing_(Jawa_Tengah)\" title=\"Gunung Sumbing (Jawa Tengah)\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">Gunung Sumbing</a>.</p><p style=\"margin: 0.5em 0px 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">Begitulah Magelang, yang kemudian berkembang menjadi kota selanjutnya menjadi ibu kota&nbsp;<a href=\"https://id.wikipedia.org/wiki/Karesidenan_Kedu\" class=\"mw-redirect\" title=\"Karesidenan Kedu\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">Karesidenan Kedu</a>&nbsp;dan juga pernah menjadi ibu kota&nbsp;<a href=\"https://id.wikipedia.org/wiki/Kabupaten_Magelang\" title=\"Kabupaten Magelang\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">Kabupaten Magelang</a>. Setelah masa kemerdekaan, kota ini menjadi kota praja dan kemudian kota madya dan pada era reformasi, sejalan dengan pemberian otonomi seluas-luasnya kepada daerah, sebutan&nbsp;<a href=\"https://id.wikipedia.org/wiki/Kotamadya\" class=\"mw-redirect\" title=\"Kotamadya\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">kota madya</a>&nbsp;ditiadakan dan diganti menjadi kota.</p><p style=\"margin: 0.5em 0px 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">Ketika Inggris menguasai Magelang pada abad ke-18, dijadikanlah kota ini sebagai pusat pemerintahan setingkat Kabupaten dan diangkatlah Mas Ngabehi&nbsp;<a href=\"https://id.wikipedia.org/wiki/Danukromo\" title=\"Danukromo\" style=\"color: var(--color-progressive,#36c); background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; border-radius: 2px; overflow-wrap: break-word;\">Danukromo</a>&nbsp;sebagai Bupati pertama. Bupati ini pulalah yang kemudian merintis berdirinya Kota Magelang dengan membangun alun-alun, bangunan tempat tinggal Bupati serta sebuah masjid. Dalam perkembangan selanjutnya, dipilihlah Magelang sebagai Ibu kota Karesidenan Kedu pada tahun 1818.</p><p style=\"margin: 0.5em 0px 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">Setelah pemerintah Inggris ditaklukkan oleh Belanda, kedudukan Magelang semakin kuat. Oleh pemerintah Belanda, kota ini dijadikan pusat lalu lintas perekonomian. Selain itu, karena letaknya yang strategis, udaranya yang nyaman, serta pemandangannya yang indah, Magelang kemudian dijadikan Kota Militer. Pemerintah Belanda terus melengkapi sarana dan prasarana perkotaan. Menara air minum dibangun di tengah-tengah kota pada tahun 1918, perusahaan listrik mulai beroperasi tahun 1927, dan jalan-jalan arteri diperkeras serta diaspal.</p><p style=\"margin: 0.5em 0px 1em;\"><font color=\"#202122\" face=\"sans-serif\">protected $fillable = [</font></p><p style=\"margin: 0.5em 0px 1em;\"><font color=\"#202122\" face=\"sans-serif\">&nbsp; &nbsp; \'title\',</font></p><p style=\"margin: 0.5em 0px 1em;\"><font color=\"#202122\" face=\"sans-serif\">&nbsp; &nbsp; \'author\',&nbsp;</font></p><p style=\"margin: 0.5em 0px 1em;\"><font color=\"#202122\" face=\"sans-serif\">&nbsp; &nbsp; \'description\',</font></p><p style=\"margin: 0.5em 0px 1em;\"><font color=\"#202122\" face=\"sans-serif\">&nbsp; &nbsp; \'date_taken\',</font></p><p style=\"margin: 0.5em 0px 1em;\"><font color=\"#202122\" face=\"sans-serif\">&nbsp; &nbsp; \'location\',</font></p><p style=\"margin: 0.5em 0px 1em;\"><font color=\"#202122\" face=\"sans-serif\">&nbsp; &nbsp; \'image_path\'</font></p><p style=\"margin: 0.5em 0px 1em;\"><font color=\"#202122\" face=\"sans-serif\">];</font></p>', 'gallery/1730877555.png', 'Hana', '2024-11-15', 'Magelang kota', '2024-11-05 21:07:44', '2024-11-06 00:20:04'),
(28, 'Hana', '<p>public function livesearch(Request $request)</p><p>{</p><p>&nbsp; &nbsp; if ($request-&gt;get(\'query\')) {</p><p>&nbsp; &nbsp; &nbsp; &nbsp; $query = $request-&gt;get(\'query\');</p><p>&nbsp; &nbsp; &nbsp; &nbsp; $galleries = Gallery::where(\'title\', \'LIKE\', \"%{$query}%\")</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -&gt;orWhere(\'description\', \'LIKE\', \"%{$query%\")</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -&gt;orWhere(\'author\', \'LIKE\', \"%{$query}%\")</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -&gt;orWhere(\'location\', \'LIKE\', \"%{$query}%\")</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -&gt;limit(5)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -&gt;get();</p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; $output = \'&lt;ul class=\"list-group\"&gt;\';</p><p>&nbsp; &nbsp; &nbsp; &nbsp; foreach ($galleries as $gallery) {</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $output .= \'</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;li class=\"list-group-item link-class\"&gt;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;img src=\"\' . asset($gallery-&gt;image_path) . \'\" height=\"50\" width=\"40\" /&gt;&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;a style=\"font-size: 16px\" href=\"\' . route(\'galeri.show\', $gallery-&gt;id) . \'\"&gt;\' . $gallery-&gt;title . \'&lt;/a&gt;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;/li&gt;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; \';</p><p>&nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp; &nbsp; $output .= \'&lt;/ul&gt;\';</p><p>&nbsp; &nbsp; &nbsp; &nbsp; echo $output;</p><p>&nbsp; &nbsp; }</p><p>}</p>', 'gallery/1730867765.png', 'Fiona', '2024-11-26', 'Kota Magelang Jawa Tengah', '2024-11-05 21:36:05', '2024-11-06 00:20:28'),
